statements.statement:
CREATE OR REPLACE FUNCTION ora_dwh.trunc_test(val NUMERIC(38,18), decimal_places NUMERIC(38,18))
RETURNS NUMERIC(38,18)
STABLE
AS $$
import or_numeric
if not val or not decimal_places:
	return None
if decimal_places:
	return or_numeric.trunc(val, decimal_places)
else:
	return or_numeric.trunc(val)
$$
LANGUAGE plpythonu
